mapper.select:
select m.m_email, m.m_nickname
from followTBL f join memberTBL m where f.followee = m.m_id and
f.follower = #{follower};

SELECT p.p_id
FROM followTBL f
JOIN postTBL p
ON f.followee = p.p_writer
WHERE f.follower = #{no}
ORDER BY p.p_id DESC;

select m.m_nickname, m.m_profilePic , m.m_code , c.*
   from commentTBL c
   join memberTBL m 
   on c.c_writer = m.m_id
   WHERE c_postNo = #{no}
   ORDER BY c.c_id DESC;
